package FindDna;

public class FindGeneWhile {
	
	public void findGene(String dna) {
		String gene = "";
		int start= dna.indexOf("ATG");;
		int current = dna.indexOf("TAA",start+3);
		while(start!=-1||current!=-1) {
		if((current-start)%3==0) {
			gene=dna.substring(start, current+3);
			System.out.println("Gene : "+gene);
			System.out.println("From : "+start);
			System.out.println("To : "+current);
			start = dna.indexOf("ATG",current);
			current = dna.indexOf("TAA",++current);
		}
		}
		if(gene=="")System.out.println("There is no gene in this dna");
	}

	public static void main(String[] args) {
		String dna = "";
		FindGeneWhile f = new FindGeneWhile();	
		f.findGene(dna);
	}
}
